<?phpnamespace Admin\Controller;use Think\Controller;class CommonController extends Controller {    public function _initialize() {                if ((!session('?uid'))&& CONTROLLER_NAME != 'Login') {            $this->redirect('Login/index');        }        $this->webtitle='卡努班级后台管理系统';        $nav = array(                        'Datamain' => array(0 => '数据维护','city'=>'城市管理','area'=>'区域管理','school'=>'学校管理','classs'=>'班级管理','teacher'=>'老师管理','import_stu_and_par_info'=>'数据导入'),           );        $this->assign('nav', $nav);        $left['数据维护'] = array('nav' => array(                            '城市管理' => array('controller'=>'Datamain','action'=>'city'),                '区域管理' => array('controller'=>'Datamain','action'=>'area'),                '学校管理' => array('controller'=>'Datamain','action'=>'school'),                '班级管理' => array('controller'=>'Datamain','action'=>'classs'),                '老师管理' => array('controller'=>'Datamain','action'=>'teacher'),                '数据导入' => array('controller'=>'Datamain','action'=>'import_stu_and_par_info'),            )                  );                    $this->assign('left', $left);    }}