<?phpnamespace Admin\Model;use Think\Model;class CityModel extends Model{    protected  $_validate=array(        array('name','require','名称必须！'),    );    protected function _before_delete($options) {        $ids    = $this->where($options['where'])->getField('id,id,name');        $Area  = M('Area');        foreach ($ids as $id=>$field)        {            $map['city_id']=$id;            if($Area->where($map)->count())            {                $this->error=$field['name'].'中存在区域';                return false;            }                    }    }   }