<?phpnamespace Common\Model;use Think\Model;class MessageModel extends Model {    protected function _after_select(&$resultSet, $options) {        foreach($resultSet as &$result)        {            if(false===$this->_after_find($result, $options))            {                return false;            }        }    }    protected function _after_find(&$result, $options) {        $result['send_member_info']=D('Member')->find($result['member_id']);        $result['comment_list']=D('MessageComment')->where(array('message_id'=>$result['id'],'reply_id'=>0,'is_delete'=>0))->order('addtime desc')->select();    }}